import React, { useState, useEffect } from "react";
import { appointmentService } from "../../api/appointmentService";

function Field({ label, children }) {
  return (
    <div className="mb-3">
      <label className="block text-sm font-medium text-gray-700">{label}</label>
      {children}
    </div>
  );
}

function CenterList({ centers, selectedId, onSelect }) {
  return (
    <div className="grid gap-3">
      {centers.map((c) => (
        <button
          key={c.id}
          onClick={() => onSelect(c)}
          className={`p-3 text-left border rounded-md ${selectedId === c.id ? "border-blue-500 bg-blue-50" : "border-gray-200 hover:bg-gray-50"}`}
        >
          <div className="font-semibold">{c.name}</div>
          <div className="text-xs text-gray-500">{c.address} • {c.distance}</div>
        </button>
      ))}
    </div>
  );
}

function ServiceList({ services, selectedIds, onToggle }) {
  if (!services) return <div>Đang tải dịch vụ...</div>;
  return (
    <div className="space-y-2">
      {services.map((s) => (
        <label key={s.id} className="flex justify-between items-center p-3 border rounded-md">
          <div className="font-medium">{s.name}</div>
          <input type="checkbox" checked={selectedIds.includes(s.id)} onChange={() => onToggle(s.id)} />
        </label>
      ))}
    </div>
  );
}

export default function BookingPage() {
  const [centers, setCenters] = useState([]);
  const [selectedCenter, setSelectedCenter] = useState(null);
  const [services, setServices] = useState([]);
  const [selectedServices, setSelectedServices] = useState([]);
  const [customerName, setCustomerName] = useState("");
  const [vehicleModel, setVehicleModel] = useState("");
  const [vin, setVin] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  useEffect(() => {
    appointmentService.getCenters().then(setCenters);
  }, []);

  useEffect(() => {
    if (selectedCenter) {
      appointmentService.getServices(selectedCenter.id).then(setServices);
    } else {
      setServices([]);
      setSelectedServices([]);
    }
  }, [selectedCenter]);

  const toggleService = (id) => {
    setSelectedServices((prev) => (prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]));
  };

  const handleBooking = async () => {
    if (!customerName || !vehicleModel || !vin || !selectedCenter || !date || !time || selectedServices.length === 0) {
      alert("Vui lòng nhập đầy đủ thông tin!");
      return;
    }
    await appointmentService.createAppointment({
      customerName,
      vehicleModel,
      vin,
      center: selectedCenter,
      services: services.filter((s) => selectedServices.includes(s.id)),
      date,
      time,
    });
    alert("Đặt lịch thành công!");
  };

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-5">
      <h1 className="text-2xl font-bold mb-3">Đặt lịch dịch vụ EV</h1>

      <div className="grid lg:grid-cols-3 gap-6">
        <div className="space-y-4">
          <div className="p-4 bg-white rounded-md border">
            <h2 className="font-semibold mb-3">Chọn trung tâm</h2>
            <CenterList centers={centers} selectedId={selectedCenter?.id} onSelect={setSelectedCenter} />
          </div>

          <div className="p-4 bg-white rounded-md border">
            <h2 className="font-semibold mb-3">Thông tin khách hàng</h2>
            <Field label="Họ tên">
              <input value={customerName} onChange={(e) => setCustomerName(e.target.value)} className="w-full p-2 border rounded-md" />
            </Field>
            <Field label="Mẫu xe">
              <input value={vehicleModel} onChange={(e) => setVehicleModel(e.target.value)} className="w-full p-2 border rounded-md" />
            </Field>
            <Field label="Mã VIN">
              <input value={vin} onChange={(e) => setVin(e.target.value)} className="w-full p-2 border rounded-md" />
            </Field>
          </div>
        </div>

        <div className="lg:col-span-2 space-y-4">
          <div className="p-4 bg-white rounded-md border">
            <h2 className="font-semibold mb-3">Chọn dịch vụ</h2>
            <ServiceList services={services} selectedIds={selectedServices} onToggle={toggleService} />
          </div>

          <div className="p-4 bg-white rounded-md border">
            <h2 className="font-semibold mb-3">Chọn thời gian</h2>
            <div className="flex gap-3">
              <input type="date" value={date} onChange={(e) => setDate(e.target.value)} className="p-2 border rounded-md" />
              <input type="time" value={time} onChange={(e) => setTime(e.target.value)} className="p-2 border rounded-md" />
            </div>
          </div>

          <div className="p-4 bg-white rounded-md border flex justify-end">
            <button onClick={handleBooking} className="px-4 py-2 bg-blue-600 text-white rounded-md">Đặt lịch</button>
          </div>
        </div>
      </div>
    </div>
  );
}
